import React, { useEffect, useState } from "react";
import { message } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { authConstants } from "../../Constants";
import { useHistory } from "react-router-dom";

import "./RegisterPage.css";
const RegisterPage = () => {
  const authenticated = useSelector((state) => state.auth.authenticated);
  const dispatch = useDispatch();
  const history = useHistory();
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await window.fetch("http://localhost:3001/v1/user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userName: userName,
          password: password,
        }),
      });
      const result = await response.json();
      if (result.error) {
        throw result.error;
      }
      dispatch({
        type: authConstants.LOAD_USER_PROFILE,
        payload: {
          id: result.success.payload.id,
          userName: result.success.payload.username,
        },
      });
      window.localStorage.setItem("token", result.success.token);
      window.localStorage.setItem(
        "user",
        JSON.stringify(result.success.payload)
      );
      message.success("Registered Successfully");
    } catch (err) {
      console.log(err);
      message.error(err.message);
    }
  };

  useEffect(() => {
    if (authenticated) {
      history.push("/");
    }
  }, [authenticated, history]);

  return (
    <div id="register-body">
      <div className="container" id="container">
        <div className="form-container sign-in-container">
          <form onSubmit={handleRegister}>
            <h1
              style={{
                margin: "20px auto",
                fontSize: "2.5em",
                fontWeight: "600",
                color: "#666",
              }}
            >
              Create a new account
            </h1>
            <input
              type="text"
              name="first-name"
              placeholder="First Name *"
              onChange={(e) => setFirstName(e.target.value)}
              value={firstName}
              required
            />
            <input
              type="text"
              name="last-name"
              placeholder="Last Name  *"
              onChange={(e) => setLastName(e.target.value)}
              value={lastName}
              required
            />
            <input
              type="number"
              name="phone"
              placeholder="Phone Number *"
              onChange={(e) => setPhoneNumber(e.target.value)}
              value={phoneNumber}
              minLength="10"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email *"
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              minLength="8"
              required
            />
            <select
              onChange={(e) => setClassId(e.target.value)}
              value={classId}
              required
            >
              <option value="">Select Class *</option>
              <option value="5fd19190-6f2e-11ea-a347-dfda97d66307">
                Pre 11
              </option>
              <option value="a18295d0-6f2e-11ea-a347-dfda97d66307">
                Class 11
              </option>
              <option value="9b6556b0-6f2e-11ea-a347-dfda97d66307">
                Class 12
              </option>
              <option value="69d9ed90-6f2e-11ea-a347-dfda97d66307">
                Post 12
              </option>
            </select>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                width: "100%",
              }}
            >
              <select
                onChange={(e) => setStateId(e.target.value)}
                value={stateId}
                style={{ width: "48%" }}
                required
              >
                <option value="">Select State *</option>
                <option value="76369680-5d2a-11ea-862d-e7441a46e727">
                  Andhra Pradesh
                </option>
                <option value="7cfff420-5d2a-11ea-862d-e7441a46e727">
                  Arunachal Pradesh
                </option>
                <option value="861a94c0-5d2a-11ea-862d-e7441a46e727">
                  Assam
                </option>
                <option value="424469c0-5d2e-11ea-862d-e7441a46e727">
                  Bihar
                </option>
                <option value="4a1d1480-5d2e-11ea-862d-e7441a46e727">
                  Chhattisgarh
                </option>
                <option value="4e9dbaa0-5d2e-11ea-862d-e7441a46e727">
                  Goa
                </option>
                <option value="53e86780-5d2e-11ea-862d-e7441a46e727">
                  Gujarat
                </option>
                <option value="5968a350-5d2e-11ea-862d-e7441a46e727">
                  Haryana
                </option>
                <option value="5daab9d0-5d2e-11ea-862d-e7441a46e727">
                  Himachal Pradesh
                </option>
                <option value="62be2a10-5d2e-11ea-862d-e7441a46e727">
                  Jharkhand
                </option>
                <option value="6837fd40-5d2e-11ea-862d-e7441a46e727">
                  Karnataka
                </option>
                <option value="6cb56f10-5d2e-11ea-862d-e7441a46e727">
                  Kerala
                </option>
                <option value="711016a0-5d2e-11ea-862d-e7441a46e727">
                  Madhya Pradesh
                </option>
                <option value="76b0abb0-5d2e-11ea-862d-e7441a46e727">
                  Maharashtra
                </option>
                <option value="7df92460-5d2e-11ea-862d-e7441a46e727">
                  Manipur
                </option>
                <option value="857c8330-5d2e-11ea-862d-e7441a46e727">
                  Meghalaya
                </option>
