import { authConstants } from "../Constants";

const initialState = {
  authenticated: false,
  user: {},
};
export default (state = { ...initialState }, action) => {
  console.log("action called", action);
  switch (action.type) {
    case authConstants.LOAD_USER_PROFILE:
      return {
        ...state,
        authenticated: true,
        user: action.payload,
      };
    case authConstants.REMOVE_USER_PROFILE:
      return {
        ...state,
        authenticated: false,
        user: {},
      };
    default:
      return state;
  }
};
